package com.javarush.mokropolov.util;import jakarta.servlet.RequestDispatcher;import jakarta.servlet.ServletException;import jakarta.servlet.http.HttpServletRequest;import jakarta.servlet.http.HttpServletResponse;import lombok.SneakyThrows;import lombok.experimental.UtilityClass;import java.io.IOException;@UtilityClasspublic class Jsp {    public static void forward(HttpServletRequest request, HttpServletResponse response, String target) throws ServletException, IOException {        target = fixTarget(target);        RequestDispatcher requestDispatcher = request.getRequestDispatcher("/WEB-INF/%s.jsp".formatted(target));        requestDispatcher.forward(request, response);    }    private static String fixTarget(String target) {        return target.replace("/", "");    }    @SneakyThrows    public static void redirect(HttpServletResponse response, String target) throws IOException {        target = fixTarget(target);        response.sendRedirect(target);    }}